package com.snda.inote.service;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.net.ConnectivityManager;import android.net.NetworkInfo;import android.util.Log;import com.snda.inote.Inote;public class ConnectionChangeReceiver extends BroadcastReceiver {    @Override    public void onReceive(Context context, Intent intent) {        ConnectivityManager cManager = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        NetworkInfo[] netInfo = cManager.getAllNetworkInfo();        for (NetworkInfo ni : netInfo) {            if (ni.getTypeName().equalsIgnoreCase("WIFI"))                if (ni.isConnected()){                    Inote.isConnected = true;                    return;                }            if (ni.getTypeName().equalsIgnoreCase("MOBILE"))                if (ni.isConnected()){                     Inote.isConnected = true;                    return;                }        }        Inote.isConnected = false;    }}