package com.snda.inote.sql;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;public class DataHelper {    private static final String DATABASE_NAME = "snda_inote.db";    private static final int DATABASE_VERSION = 11;    public static String CATEGORY_TABLE = "categories";    public static String NOTE_TABLE = "notes";    public static String SYNC_TABLE = "syncs";    public static String ATTACH_TABLE = "attachs";    private SQLiteDatabase db;    public DataHelper(Context _context) {        OpenHelper openHelper = new OpenHelper(_context);        this.db = openHelper.getWritableDatabase();        db.setVersion(DATABASE_VERSION);    }    public SQLiteDatabase getDb() {        return db;    }    private static class OpenHelper extends SQLiteOpenHelper {        public OpenHelper(Context context) {            super(context, DATABASE_NAME, null, DATABASE_VERSION);        }        @Override        public void onCreate(SQLiteDatabase db) {            db.execSQL("CREATE TABLE " + CATEGORY_TABLE + "(_id INTEGER PRIMARY KEY, id varchar(100), uid varchar(100), pid varchar(100), name varchar(100), type INTEGER, ndefault INTEGER, count INTEGER, cached INTEGER)");            db.execSQL("CREATE TABLE " + NOTE_TABLE + "(_id INTEGER PRIMARY KEY, id varchar(100), uid varchar(100), cateid varchar(100), title varchar(100), desc varchar(100), time varchar(20), content TEXT, tag varchar(100), cached INTEGER,hasattachments INTEGER,cate_id INTEGER)");            db.execSQL("CREATE TABLE " + SYNC_TABLE + "(_id INTEGER PRIMARY KEY, uid varchar(100), entityId varchar(100), category INTEGER, operation INTERGER,type INTERGER)");            db.execSQL("CREATE TABLE " + ATTACH_TABLE + "(_id INTEGER PRIMARY KEY, uid varchar(100), noteid varchar(100),note_local_id INTEGER, filename varchar(100),filepath varchar(100),filesize INTEGER,filetype INTEGER,time varchar(20))");        }        @Override        public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {            db.execSQL("DROP TABLE IF EXISTS " + CATEGORY_TABLE);            db.execSQL("DROP TABLE IF EXISTS " + NOTE_TABLE);            db.execSQL("DROP TABLE IF EXISTS " + SYNC_TABLE);            db.execSQL("DROP TABLE IF EXISTS " + ATTACH_TABLE);            onCreate(db);        }    }}