package com.snda.inote.util;import java.text.SimpleDateFormat;import java.util.Date;import java.util.regex.Matcher;import java.util.regex.Pattern;public class StringUtil {	public static String left(String str, int num, String end){		if(str.length() <= num){			return str;		}		str = str.substring(0, num);		return str + end;	}		public static String arrayToString2(String[] a, String separator) {	    StringBuffer result = new StringBuffer();	    if (a.length > 0) {	        result.append(a[0]);	        for (int i=1; i<a.length; i++) {	            result.append(separator);	            result.append(a[i]);	        }	    }	    return result.toString();	}		public static String[] strToArray(String a, String separator){        return a.split(separator);	}    public static boolean hasText(String str){        return str!=null && str.trim().length()>0;    }    public static String getDataFormatFileName(String key) {        SimpleDateFormat d = new SimpleDateFormat("yyyy_MM_dd_hh_mm");        String format = d.format(new Date());        return key + format;    }    public static boolean isRichText(String content) {        Pattern p = Pattern.compile("<[^>]*>");        Matcher m = p.matcher(content);        return m.find();    }}